mapper.select:
SELECT COUNT(*)
FROM member
WHERE id = #{id}

SELECT pass
FROM member
WHERE id = #{id}

mapper.update:
UPDATE member
SET pass =
#{password},email = #{email},address = #{address},cell =
#{cell},home =
#{home}
WHERE id = #{id}

mapper.select:
SELECT *
FROM member
WHERE id = #{id}
AND pass =
#{password}

