mapper.insert:
update
sitlib.bookmst
set imageurl = #{fileName}
where
book_cd = #{book_cd}

mapper.select:
SELECT A.title, A.author, A.publish, A.b_group, A.rentchk, A.rcmdChk, A.imageurl, A.reservechk, A.book_cd, B.returndate FROM sitlib.bookmst A LEFT OUTER JOIN sitlib.bookrent B ON A.book_cd = B.book_cd AND A.rentchk = B.rentchk WHERE LCASE(A.TITLE) like #{keyword}
AND left(A.b_group,1) = #{b_group}
AND left(A.c_group,3) = #{c_group}
ORDER BY a.book_cd desc FETCH first 200 rows only

mapper.update:
UPDATE sitlib.bookmst
SET
rcmdChk=#{rcmdChk}
WHERE BOOK_CD=#{book_cd}
