mapper.insert:
update
sitlib.bookmst
set imageurl = #{fileName}
where
book_cd = #{book_cd}

mapper.select:
SELECT A.title, A.author, A.publish, A.b_group, A.rentchk, A.rcmdChk, A.imageurl, A.reservechk, A.book_cd, B.returndate FROM sitlib.bookmst A LEFT OUTER JOIN sitlib.bookrent B ON A.book_cd = B.book_cd AND A.rentchk = B.rentchk WHERE UCASE(A.TITLE) like #{keyword}
AND left(A.b_group,1) = #{b_group}
AND left(A.c_group,3) = #{c_group}
ORDER BY a.rcv_date desc FETCH first 200 rows only

mapper.update:
UPDATE sitlib.bookmst
SET
rcmdChk=#{rcmdChk}
WHERE BOOK_CD=#{book_cd}

mapper.select:
SELECT
coalesce(RIGHT(TRIM('000'||CHAR(MAX(SUBSTR(BOOK_CD, 6, 4)) + 1)),4),'0001') MAXSER 
FROM SITLIB.BOOKMST
WHERE 
 b_group= #{b_group}
AND  c_group = #{c_group}
