mapper.select:
SELECT COUNT(*)
FROM BOOK_MEMBER
WHERE id = #{id}

SELECT pass
FROM BOOK_MEMBER
WHERE id = #{id}

mapper.update:
UPDATE BOOK_MEMBER
SET pass =
#{password},email = #{email},address = #{address},cell =
#{cell},home =
#{home}
WHERE id = #{id}

mapper.select:
SELECT *
FROM BOOK_MEMBER
WHERE id = #{id}
AND pass =
#{password}

SELECT *
FROM BOOK_BOARD
ORDER BY registerdate;

SELECT MAX(grade)
FROM
BOOKMST;

mapper.update:
UPDATE BOOKMST
SET
bookname=#{bookname}, genre=#{genre},
publisher=#{publisher},writer=#{writer}
WHERE bookCode=#{bookCode}

mapper.select:
SELECT *
FROM BOOK_REQUEST
WHERE bookCode=#{bookCode}

mapper.update:
UPDATE BOOK_MEMBER SET numborrow =
numborrow + 1
WHERE id = #{id}

mapper.select:
SELECT reservecheck
FROM BOOKMST
WHERE bookCode=#{bookCode}

SELECT *
FROM BOOKMST AS A, reservation AS B
WHERE B.bookCode=#{bookCode}
AND
B.id=#{id}
ORDER BY
B.reservedate DESC;
