mapper.select:
SELECT COUNT(*)
FROM BOOKMEMBER
WHERE id = #{id}

SELECT pass
FROM BOOKMEMBER
WHERE id = #{id}

mapper.update:
UPDATE BOOKMEMBER
SET pass =
#{password},email = #{email},address = #{address},cell =
#{cell},home =
#{home}
WHERE id = #{id}

mapper.select:
SELECT *
FROM BOOKMEMBER
WHERE id = #{id}
AND pass =
#{password}

SELECT reservecheck
FROM BOOKMST
WHERE bookCode=#{bookCode}

SELECT *
FROM BOOKMST AS A, reservation AS B
WHERE B.bookCode=#{bookCode}
AND
B.id=#{id}
ORDER BY
B.reservedate DESC;
